<template>  <div class="location">    <h2 class="location__title">      Где отмечаем?    </h2>    <div class="location__text">      <span>Шатёр “Emerald”</span>      <span>Музей-Заповедник “Горки Ленинские”</span>      <span>Подъехать надо на КПП №3</span>      <span><b>Вас там встретят</b></span>      <span class="location__container">        <span> Координаты: </span>        <button @click="copyCoords" class="location__copy">          <span> {{ COORDINATES }} </span>          <IcoCopy class="location__ico" />        </button>        <span            v-if="isCopiedCoords"            class="location__copied"        >          Скопировано        </span>      </span>    </div>    <a        href="https://yandex.ru/maps/-/CHVwVDiT"        target="_blank"        class="location__btn"    >      Открыть карту    </a>    <div class="location__picture">      <img          src="@/assets/location-tent.jpg"          alt="Шатёр"          class="location__img"      >      <IcoHeartMain          reverse          animDuration="1.7s"          rotateZ="-10deg"          class="location__heart location__heart--1"      />      <IcoHeartSecond          dark          reverse          animDuration="1.6s"          class="location__heart location__heart--2"      />    </div>  </div></template><script setup>import IcoHeartMain from "@/components/icons/IcoHeartMain.vue";import IcoHeartSecond from "@/components/icons/IcoHeartSecond.vue";import {ref} from "vue";import IcoCopy from "@/components/icons/IcoCopy.vue";const COORDINATES = '55.499516, 37.761682';const isCopiedCoords = ref(false);const copyCoords = async () => {  try {    await navigator.clipboard.writeText(COORDINATES);    isCopiedCoords.value = true;    setTimeout(() => {      isCopiedCoords.value = false;    }, 3000);  } catch(e) {    alert('Ошибка копирования координат');  }}</script><style scoped lang="scss">.location {  position: relative;  text-align: center;  padding-bottom: 1rem;  &__title {    font-family: 'Unutterable', 'Arial', sans-serif;    letter-spacing: 0.3rem;    font-weight: 700;    font-size: 1rem;    color: vars.$main_color;  }  &__text {    display: flex;    flex-direction: column;    color: vars.$dark_color;    letter-spacing: 0.3px;    font-size: 0.75rem;    margin-bottom: 0.7rem;  }  &__copy {    position: relative;    display: flex;    gap: 0.3rem;    border: none;    background-color: rgba(vars.$dark_color, 0.1);    border-radius: 0.2rem;    color: vars.$dark_color;    padding-left: 0.3rem;    padding-right: 0.3rem;  }  &__ico {    display: flex;    justify-content: center;    align-items: center;    width: 1rem;    height: 1rem;  }  &__btn {    font-size: 0.75rem;    text-decoration: none;    color: vars.$dark_color;    border: 1px solid vars.$dark_color;    padding: 0.1rem 0.6rem;  }  &__picture {    position: relative;    width: 17rem;    margin-left: auto;    margin-right: auto;    margin-top: 1rem;    padding-right: 1rem;  }  &__img {    width: 100%;    height: 100%;    object-fit: contain;    object-position: center;  }  &__heart {    position: absolute;    &--1 {      width: 0.75rem;      top: -1rem;      left: 2rem;    }    &--2 {      width: 0.8rem;      right: -0.3rem;      bottom: 2rem;    }  }    &__container {    margin-top: 0.3rem;    position: relative;    display: flex;    justify-content: center;    gap: 0.2rem;    * {      display: flex;      align-items: center;    }  }  &__copied {    position: absolute;    z-index: 10;    bottom: -0.64rem;    left: 10.4rem;    font-size: 0.5rem;    color: vars.$main_color;    font-weight: 700;    text-transform: uppercase;  }}</style>