<template>  <div class="timer">    <div class="timer__picture">      <img          src="@/assets/timer.jpg"          alt="Мы ждём свадьбу"          class="timer__img"      >    </div>    <div class="timer__content">      <div class="timer__container">        <h2 class="timer__title">          До свадьбы <br> осталось:        </h2>        <IcoHeartSecond class="timer__heart timer__heart--1" />        <IcoHeartMain            rotateZ="10deg"            class="timer__heart timer__heart--2"        />      </div>      <VueCountdown          :time="timeLeft"          v-slot="{days, hours, minutes, seconds}"          class="timer__countdown timer-countdown"      >        <div class="timer-countdown__item">          <span class="timer-countdown__num">{{ days }}</span>          <span class="timer-countdown__text">{{ getPluralForm(days, WORDS.DAYS) }}</span>        </div>        <div class="timer-countdown__item">          <span class="timer-countdown__num">{{ hours }}</span>          <span class="timer-countdown__text">{{ getPluralForm(hours, WORDS.HOURS) }}</span>        </div>        <div class="timer-countdown__item">          <span class="timer-countdown__num">{{ minutes }}</span>          <span class="timer-countdown__text">{{ getPluralForm(minutes, WORDS.MINUTES) }}</span>        </div>        <div class="timer-countdown__item">          <span class="timer-countdown__num">{{ seconds }}</span>          <span class="timer-countdown__text">{{ getPluralForm(seconds, WORDS.SECONDS) }}</span>        </div>      </VueCountdown>    </div>  </div></template><script setup>import VueCountdown from '@chenfengyuan/vue-countdown';import IcoHeartSecond from "@/components/icons/IcoHeartSecond.vue";import IcoHeartMain from "@/components/icons/IcoHeartMain.vue";const TIMESTAMP_NOW = new Date().getTime();const TIMESTAMP_FINISH = new Date('Jun 29 2025 15:00:00 GMT+0300').getTime();const WORDS = {  DAYS: ['день', 'дня', 'дней'],  HOURS: ['час', 'часа', 'часов'],  MINUTES: ['минута', 'минуты', 'минут'],  SECONDS: ['секунда', 'секунды', 'секунд'],};const timeLeft = TIMESTAMP_FINISH - TIMESTAMP_NOW;const getPluralForm = (count, titles) => {  const cases = [2, 0, 1, 1, 1, 2];  return titles[count % 100 > 4 && count % 100 < 20 ? 2 : cases[count % 10 < 5 ? count % 10 : 5]];};</script><style scoped lang="scss">.timer {  display: flex;  padding-bottom: 2rem;  &__container {    position: relative;  }  &__picture {    margin-left: -2.5rem;    flex-shrink: 0;    width: 12rem;    transform: rotateZ(15deg);  }  &__img {    width: 100%;    height: 100%;    object-fit: contain;    object-position: center;  }  &__content {    margin-left: -1rem;    position: relative;    z-index: 1;    width: 100%;    display: flex;    flex-direction: column;    justify-content: center;  }  &__title {    font-family: 'Unutterable', 'Arial', sans-serif;    font-size: 0.7rem;    line-height: 1.6;    letter-spacing: 0.2rem;    text-align: center;  }  &__countdown {    margin-top: 0.2rem;  }  &__heart {    position: absolute;    &--1 {      width: 0.6rem;      top: -0.8rem;      left: 1.5rem;    }    &--2 {      width: 0.77rem;      bottom: 0.8rem;      right: 1rem;    }  }}.timer-countdown {  display: flex;  justify-content: space-between;  padding-left: 0.5rem;  padding-right: 0.7rem;  &__item {    display: flex;    flex-direction: column;    align-items: center;    min-width: 2.6rem;    color: vars.$dark_color;    opacity: 0.9;  }  &__num {    font-size: 0.9rem;  }  &__text {    text-transform: uppercase;    font-size: 0.5rem;  }}</style>