<template>  <main      v-if="isMobile"      class="home-view"  >    <Intro class="home-view__intro" />    <Welcome        data-aos="fade-up"        data-aos-duration="1000"        class="home-view__welcome"    />    <Location        data-aos="fade-up"        data-aos-duration="1000"        class="home-view__location"    />    <EventPlan        data-aos="fade-up"        data-aos-duration="1000"        class="home-view__plan home-view__plan--test"    />    <Timer        data-aos="fade-up"        data-aos-duration="1000"        class="home-view__timer"    />    <DressCode        data-aos="fade-up"        data-aos-duration="1000"        class="home-view__dresscode"    />    <Details        data-aos="fade-up"        data-aos-duration="1000"        class="home-view__details"    />    <Questionnaire        data-aos="fade-up"        data-aos-duration="1000"        class="home-view__questionnaire"    />  </main>  <div      v-else      class="home-view-qrcode"  >    <div class="home-view-qrcode__text">      Для просмотра сайта-приглашения наведите камеру на qr-код    </div>    <IcoQrCode class="home-view-qrcode__ico" />  </div></template><script setup>import Intro from "@/components/Intro.vue";import Welcome from "@/components/Welcome.vue";import Location from "@/components/Location.vue";import EventPlan from "@/components/EventPlan.vue";import Timer from "@/components/Timer.vue";import DressCode from "@/components/DressCode.vue";import Details from "@/components/Details.vue";import Questionnaire from "@/components/Questionnaire.vue";import {computed, ref} from "vue";import IcoQrCode from "@/components/icons/IcoQrCode.vue";const isMobile = ref(window.outerWidth < 567);window.addEventListener('resize', () => {  if (window.outerWidth < 567) {    isMobile.value = true;    return;  }  isMobile.value = false;});</script><style lang="scss">@use "@/styles/_normalize";@use "@/styles/_fonts";</style><style scoped lang="scss">.home-view {  background-color: vars.$bg_color;  font-family: Arial, sans-serif;  width: 100%;  overflow: hidden;  &__welcome {    margin-top: 1.7rem;  }  &__location {    margin-top: 3rem;  }  &__details {    margin-top: 3rem;  }}.home-view-qrcode {  height: 100vh;  width: 100vw;  min-height: 815px;  display: flex;  flex-direction: column;  align-items: center;  justify-content: center;  &__ico {    flex-shrink: 0;    width: 500px;    height: 500px;  }  &__text {    font-family: Arial, sans-serif;    font-size: 32px;    text-align: center;    text-transform: uppercase;    width: 770px;  }}</style>